import json
import re
from typing import Any, Dict, Optional, Iterable, List
from datetime import datetime
from urllib.parse import urlparse

def stringify_references(references: Dict[str, Any]) -> Optional[str]:
    """
    从读取 references，并序列化为“论文参考文献式”的长文本（换行拼接）。
    - 若为 str：去空格后直接返回；
    - 若为 list/dict：转成按序编号的参考文献条目文本；
    - 若为空或不存在：返回 None。

    支持的条目字段：
      - title: 必选（若缺失则用“（无标题）”）
      - publish_time: 可选（尝试标准化为 YYYY-MM-DD）
      - url: 可选（用于输出“可获取于”并解析来源域名）
      - idx_val: 可选（若存在，用于排序；否则保持原有顺序）
    """
    print(f"开始解析:references:\n{references}")
    if references is None:
        return None
    if isinstance(references, str):
        s = references.strip()
        return s if s else None

    # 将 references 统一摊平成条目列表
    items: List[Dict[str, Any]] = []
    if isinstance(references, dict):
        for k, v in references.items():
            if isinstance(v, dict):
                entry = dict(v)  # 浅拷贝，避免修改原对象
                entry.setdefault("file_id", k)
                items.append(entry)
            else:
                # 容错：非 dict 的值也尽量转成条目
                items.append({"file_id": k, "title": str(v)})
    elif isinstance(references, list):
        for v in references:
            if isinstance(v, dict):
                items.append(v)
            else:
                items.append({"title": str(v)})
    else:
        # 其他类型兜底：尽量转字符串
        try:
            return json.dumps(references, ensure_ascii=False)
        except Exception:
            return str(references)

    if not items:
        return None

    # 是否存在 idx_val；有则按 idx_val 排序，否则保留原顺序
    has_idx = any("idx_val" in it and it.get("idx_val") is not None for it in items)

    def _idx_safe(val):
        try:
            return int(val)
        except Exception:
            return float("inf")

    if has_idx:
        items.sort(key=lambda d: (_idx_safe(d.get("idx_val")), str(d.get("publish_time") or "")))

    today = datetime.now().strftime("%Y-%m-%d")

    def _norm_date(s: Any) -> Optional[str]:
        """尽量把日期标准化为 YYYY-MM-DD；识别 ISO/常见格式，失败则返回 None。"""
        if not s:
            return None
        if isinstance(s, (int, float)):
            try:
                # 兼容时间戳（秒）
                return datetime.fromtimestamp(float(s)).strftime("%Y-%m-%d")
            except Exception:
                return None
        s = str(s).strip()
        if not s:
            return None
        # 尝试匹配 YYYY-MM-DD
        m = re.match(r"^\s*(\d{4})-(\d{1,2})-(\d{1,2})", s)
        if m:
            y, mo, d = m.groups()
            try:
                return datetime(int(y), int(mo), int(d)).strftime("%Y-%m-%d")
            except Exception:
                pass
        # 尝试匹配 YYYY/MM/DD
        m = re.match(r"^\s*(\d{4})/(\d{1,2})/(\d{1,2})", s)
        if m:
            y, mo, d = m.groups()
            try:
                return datetime(int(y), int(mo), int(d)).strftime("%Y-%m-%d")
            except Exception:
                pass
        # 尝试从 ISO8601 中抽取日期
        m = re.match(r"^\s*(\d{4})-(\d{2})-(\d{2})[T\s]", s)
        if m:
            y, mo, d = m.groups()
            try:
                return datetime(int(y), int(mo), int(d)).strftime("%Y-%m-%d")
            except Exception:
                pass
        return None

    lines = []
    for i, it in enumerate(items, 1):
        title = (it.get("title") or "").strip() or "（无标题）"
        url = (it.get("url") or "").strip() or None
        date_str = _norm_date(it.get("publish_time"))
        src = None
        if url:
            try:
                netloc = urlparse(url).netloc
                # 去掉常见的 www 前缀
                src = netloc[4:] if netloc.startswith("www.") else netloc
            except Exception:
                src = None

        # 参考文献行（简化 GB/T：电子文献 EB/OL）
        # 结构：[序号] 标题[EB/OL]. 来源，发布于 日期。可获取于：URL（访问日期：YYYY-MM-DD）
        segs = []
        head = f"[{i}] {title}[EB/OL]"
        if src and date_str:
            segs.append(f"{src}，发布于 {date_str}")
        elif src:
            segs.append(src)
        elif date_str:
            segs.append(f"发布于 {date_str}")
        body = "，".join(segs)
        tail = []
        if url:
            tail.append(f"From：{url}")
        # tail.append(f"（访日期：{today}）")

        # 组装并清理多余空格/标点
        line = head
        if body:
            line += f". {body}"
        if tail:
            line += f"。{' '.join(tail)}"
        lines.append(line)

    # 以换行拼接成一个长文本
    result = "\n# 参考文献:\n\n" + "\n\n".join(lines).strip()
    return result if result else None

